  const mainDivRef = useRef(null);
  const mainRef = useRef(null);

  useGSAP(() => {
    if (mainDivRef.current === null) return;

    let curr = mainDivRef.current;
    let children = curr.children;
    if (!children) return;
    let imageDivs = gsap.utils.toArray(".img-holder");
    if (imageDivs.length === 0) return;

    gsap.to(".loader", {
      clipPath: "inset(0% 0% 0% 100%)",
      duration: 2,
      delay: 3,
      ease: "power3.out",
    });

    let tl = gsap.timeline({
      defaults: {
        ease: "none",
      },
      scrollTrigger: {
        trigger: ".main-div",
        start: "top top",
        end: "+=" + mainRef.current.offsetWidth * 9,
        scrub: 3,
        snap: {
          snapTo: 1 / (imageDivs.length - 1),
          duration: { min: 0.5, max: 0.8 },
        },
        pin: ".main",
        pinSpacing: true,
      },
    });

    tl.fromTo(
      ".main-div",
      {
        xPercent: 0,
      },
      {
        xPercent: -89,
      },
    );

    imageDivs.forEach((div, index) => {
      tl.from(div, {
        scrollTrigger: {
          trigger: div,
          start: "left center",
          end: "right center",
          containerAnimation: tl,
          onEnter: () => {
            setColor(images[index].color);
          },
          onEnterBack: () => {
            setColor(images[index].color);
          },
        },
      });
    });
  });

